INSERT INTO `micro_templates` VALUES 
(1,'categories-box','{if $content_list}\r\n<section class=\"widget inner categories-widget\">\r\n	<h3 class=\"widget-title\">{lang}Categories{/lang}</h3>\r\n		<ul class=\"icons clearfix\">\r\n			{foreach from=$content_list item=node}\r\n				<li{if $node.alias == $content_alias} class=\"active\"{/if}><a href=\"{$node.url}\">{$node.name}</a></li>\r\n			{/foreach}\r\n		</ul>\r\n</section>\r\n{/if}','2013-10-01 17:08:06','2013-10-08 20:03:31','content_listing'),
(2,'info-links','<ul class=\"nav pull-left\">\r\n	<li{if $content_alias == \'home-page\'} class=\"active\"{/if}><a href=\"{base_path}/\">{lang}Home{/lang}</a></li>\r\n	<li class=\"dropdown\">\r\n		<a data-toggle=\"dropdown\" class=\"dropdown-toggle\" href=\"\">{lang}Information{/lang} <b class=\"caret\"></b></a>\r\n			<ul class=\"dropdown-menu\">\r\n				{foreach from=$content_list item=node}\r\n					<li{if $node.alias == $content_alias} class=\"active\"{/if}><a href=\"{$node.url}\">{$node.name}</a></li>\r\n				{/foreach}\r\n			</ul>\r\n	</li>\r\n	{admin_area_link}\r\n</ul>','2013-10-01 17:08:06','2013-10-08 21:00:31','content_listing'),
(3,'subcategory-listing','{if $content_list}\r\n<div class=\"row-fluid featured-categories\">\r\n	<ul class=\"thumbnails\">\r\n	{foreach from=$content_list item=node}\r\n		<li class=\"span4 item\">\r\n			<div class=\"thumbnail\">\r\n				<a href=\"{$node.url}\" class=\"image\">\r\n				<img src=\"{$node.image}\" alt=\"{$node.name}\" title=\"{$node.name}\"{if isset($thumbnail_width)} width=\"{$thumbnail_width}\"{/if} />\r\n				<span class=\"frame-overlay\"></span>\r\n				<h4 class=\"title\"><i class=\"icon-folder-open\"></i> {$node.name}</h4>\r\n				<span class=\"link\">{lang}view products{/lang} <i class=\"icon-chevron-right\"></i></span>\r\n				</a>\r\n\r\n				<div class=\"inner notop\">\r\n					<div class=\"description\">\r\n						{$node.description|strip_tags|truncate:120:\"...\":true}\r\n					</div>\r\n				</div>\r\n			</div>\r\n		</li>\r\n	{/foreach}\r\n	</ul>\r\n</div>\r\n{else}\r\n<div>{lang}No Items Found{/lang}</div>\r\n{/if}\r\n','2013-10-01 17:08:06','2013-10-20 14:59:24','content_listing'),
(4,'product-listing','{if $content_list}\r\n\r\n{if $pages_number > 1}\r\n<!-- start: Pagination -->\r\n<div class=\"pagination pagination-centered\">\r\n	<ul>\r\n		{for $pg=1 to $pages_number}\r\n		<li{if $pg == $page} class=\"active\"{/if}><a href=\"{base_path}/category/{$content_alias->value}{$ext}/page/{$pg}\">{$pg}</a></li>\r\n		{/for}\r\n		<li{if \"all\" == $page} class=\"active\"{/if}><a href=\"{base_path}/category/{$content_alias->value}{$ext}/page/all\">{lang}All{/lang}</a></li>\r\n	</ul>\r\n</div>\r\n<!-- end: Pagination -->\r\n{/if}  \r\n  \r\n<!-- start: products listing -->\r\n<div class=\"row-fluid shop-products\">\r\n	<ul class=\"thumbnails\">\r\n		{foreach from=$content_list item=node}\r\n		<li class=\"item span4 {if $node@index is div by 3}first{/if}\">\r\n			<div class=\"thumbnail\">\r\n				<a href=\"{$node.url}\" class=\"image\"><img src=\"{$node.image}\" alt=\"{$node.name}\"{if isset($thumbnail_width)} width=\"{$thumbnail_width}\"{/if} /><span class=\"frame-overlay\"></span><span class=\"price\">{$node.price}</span></a>\r\n			<div class=\"inner notop nobottom\">\r\n				<h4 class=\"title\">{$node.name}</h4>\r\n				<div class=\"description\">{$node.description|strip_tags|truncate:30:\"...\":true}</div>\r\n				<div class=\"description\">{attribute_list value_attributes=$node.attributes}</div>\r\n			</div>\r\n			</div>\r\n			{product_form product_id={$node.id}}\r\n			<div class=\"inner darken notop\">\r\n				<button class=\"btn btn-add-to-cart\" type=\"submit\"><i class=\"icon-shopping-cart\"></i> {lang}Buy{/lang}</button>\r\n				{if isset($is_compare)}<a href=\"{base_path}/category/addcmp/{$node.alias}/{$content_alias->value}{$ext}\" class=\"btn btn-add-to-cart\"><i class=\"icon-bookmark\"></i> {lang}Compare{/lang}</a>{/if}\r\n			</div>\r\n			{/product_form}\r\n		</li>\r\n		{/foreach}\r\n	</ul>\r\n</div>  \r\n<!-- end: products listing -->\r\n\r\n{if $pages_number > 1}\r\n<!-- start: Pagination -->\r\n<div class=\"pagination pagination-centered\">\r\n	<ul>\r\n		{for $pg=1 to $pages_number}\r\n		<li{if $pg == $page} class=\"active\"{/if}><a href=\"{base_path}/category/{$content_alias->value}{$ext}/page/{$pg}\">{$pg}</a></li>\r\n		{/for}\r\n		<li{if \"all\" == $page} class=\"active\"{/if}><a href=\"{base_path}/category/{$content_alias->value}{$ext}/page/all\">{lang}All{/lang}</a></li>\r\n	</ul>\r\n</div>\r\n<!-- end: Pagination -->\r\n{/if}\r\n\r\n{else}\r\n{lang}No Items Found{/lang}\r\n\r\n{/if}  ','2013-10-01 17:08:06','2013-10-18 11:30:38','content_listing'),
(5,'news-dropdown','<li class=\"dropdown\">\r\n	<a class=\"dropdown-toggle\" data-toggle=\"dropdown\" href=\"\">{lang}News{/lang} <b class=\"caret\"></b></a>\r\n		<ul class=\"dropdown-menu\">\r\n			{foreach from=$content_list item=node}\r\n				<li{if $node.alias == $content_alias} class=\"active\"{/if}><a href=\"{$node.url}\">{$node.name}</a></li>\r\n			{/foreach}\r\n		</ul>\r\n</li>','2013-10-08 12:05:54','2013-10-17 11:46:25','content_listing'),
(6,'search-box','<section class=\"widget inner darken search\">\r\n	<form id=\"search\" class=\"input-append\" action=\"{base_path}/page/search-result.html\" method=\"get\">\r\n		<input class=\"span4\" id=\"appendedInputButton\" name=\"keyword\" type=\"text\" placeholder=\"{lang}Search{/lang}\" />\r\n		<input class=\"btn search-bt\" type=\"submit\" value=\"\" />\r\n	</form>\r\n</section>','2013-10-01 17:08:06','2013-10-07 16:25:08','content_listing'),
(7,'cart-content-box','<div id=\"shopping-cart-box\">\r\n{if $order_items}\r\n<section class=\"widget inner shopping-cart-widget\">\r\n	<h3 class=\"widget-title\">{lang}Shopping Cart{/lang}</h3>\r\n		<div class=\"products\">\r\n			{foreach from=$order_items item=product}\r\n				<div class=\"media\">\r\n					<a class=\"pull-right\" href=\"{$product.url}\">\r\n						<img class=\"media-object\" src=\"{$product.image.image_thumb}\" alt=\"\" title=\"\" />\r\n					</a>\r\n				<div class=\"media-body\">\r\n                  <h4 class=\"media-heading\"><a href=\"{$product.url}\">{$product.name}</a> <a href=\"{base_path}/cart/remove_product/{$product.id}/1\" class=\"remove\" title=\"{lang}Remove{/lang}\"><i class=\"icon-trash\"></i></a></h4>\r\n					{$product.qty} x {$product.price}\r\n				</div>\r\n				</div>\r\n			{/foreach}\r\n		</div>\r\n		<p class=\"subtotal\">\r\n			<strong>{lang}Total{/lang}:</strong>\r\n			<span class=\"amount\">{$order_total}</span>\r\n		</p>\r\n		<p class=\"buttons\">\r\n			<a class=\"btn btn-inverse viewcart\" href=\"{$cart_link}\"><i class=\"icon-shopping-cart\"></i> {lang}View Cart{/lang}</a>\r\n			<a class=\"btn btn-inverse checkout\" href=\"{$checkout_link}\"><i class=\"icon-ok\"></i> {lang}Checkout{/lang} &rarr;</a>\r\n		</p>\r\n</section>\r\n{/if}\r\n</div>','2013-10-01 17:08:06','2013-10-17 23:02:41','shopping_cart'),
(8,'cart-content-box-ajax','{if $order_items}\r\n<section class=\"widget inner shopping-cart-widget\">\r\n	<h3 class=\"widget-title\">{lang}Shopping Cart{/lang}</h3>\r\n		<div class=\"products\">\r\n			{foreach from=$order_items item=product}\r\n				<div class=\"media\">\r\n					<a class=\"pull-right\" href=\"{$product.url}\">\r\n						<img class=\"media-object\" src=\"{$product.image.image_thumb}\" alt=\"\" title=\"\" />\r\n					</a>\r\n				<div class=\"media-body\">\r\n					<h4 class=\"media-heading\"><a href=\"{$product.url}\">{$product.name}</a> <a href=\"{base_path}/cart/remove_product/{$product.id}/1\" class=\"remove\" title=\"{lang}Remove{/lang}\"><i class=\"icon-trash\"></i></a></h4>\r\n					{$product.qty} x {$product.price}\r\n				</div>\r\n				</div>\r\n			{/foreach}\r\n		</div>\r\n		<p class=\"subtotal\">\r\n			<strong>{lang}Total{/lang}:</strong>\r\n			<span class=\"amount\">{$order_total}</span>\r\n		</p>\r\n		<p class=\"buttons\">\r\n			<a class=\"btn btn-inverse viewcart\" href=\"{$cart_link}\"><i class=\"icon-shopping-cart\"></i> {lang}View Cart{/lang}</a>\r\n			<a class=\"btn btn-inverse checkout\" href=\"{$checkout_link}\"><i class=\"icon-ok\"></i> {lang}Checkout{/lang} &rarr;</a>\r\n		</p>\r\n</section>\r\n{/if}','2013-10-01 17:08:06','2013-10-17 23:01:27','shopping_cart'),
(9,'cart-content-box-pull','{if $order_items}\r\n<div class=\"shopping-cart pull-right\">\r\n	<a href=\"{$cart_link}\" class=\"cart\">\r\n		<span class=\"quantity\">{$total_quantity}</span>\r\n		<span class=\"amount\"><i class=\"icon-shopping-cart\"></i>{$order_total}</span>\r\n	</a>\r\n	<div class=\"cart-dropdown\">\r\n		<h2 class=\"title\">{lang}Cart{/lang}</h2>\r\n			<div class=\"content\">\r\n				<div class=\"products\">\r\n					{foreach from=$order_items item=product}\r\n						<div class=\"media\">\r\n							<a class=\"pull-right\" href=\"{$product.url}\">\r\n								<img class=\"media-object\" src=\"{$product.image.image_thumb}\" alt=\"\" title=\"\" />\r\n							</a>\r\n							<div class=\"media-body\">\r\n								<h4 class=\"media-heading\"><a href=\"{$product.url}\">{$product.name}</a> <a href=\"{base_path}/cart/remove_product/{$product.id}/1\" class=\"remove\" title=\"{lang}Remove{/lang}\"><i class=\"icon-trash\"></i></a></h4>\r\n								{$product.qty} x {$product.price}\r\n							</div>\r\n						</div>\r\n					{/foreach}\r\n				</div>\r\n				<p class=\"subtotal\">\r\n					<strong>{lang}Total{/lang}:</strong>\r\n					<span class=\"amount\">{$order_total}</span>\r\n				</p>\r\n				<p class=\"buttons\">\r\n					<a class=\"btn btn-inverse viewcart\" href=\"{$cart_link}\"><i class=\"icon-shopping-cart\"></i> {lang}View Cart{/lang}</a>\r\n					<a class=\"btn btn-inverse viewcart\" href=\"{$checkout_link}\"><i class=\"icon-ok\"></i> {lang}Checkout{/lang} &rarr;</a>\r\n				</p>\r\n			</div>\r\n	</div>\r\n</div>\r\n{/if}','2013-10-07 16:49:09','2013-10-17 23:01:32','shopping_cart'),
(10,'cart-confirm-view','{if $order_items}\r\n<div class=\"cart\">\r\n<h3>{lang}Cart Contents{/lang}</h3>\r\n  <table class=\"contentTable\">\r\n		<tr>\r\n			<th>{lang}Product{/lang}</th>\r\n			<th>{lang}Price Ea.{/lang}</th>\r\n			<th>{lang}Qty{/lang}</th>\r\n			<th>{lang}Total{/lang}</th>\r\n		</tr>\r\n\r\n{foreach from=$order_items item=product}\r\n		<tr>\r\n			<td><a href=\"{$product.link}\">{$product.name}</a></td>\r\n			<td>{$product.price}</td>\r\n			<td>{$product.qty}</td>\r\n			<td>{$product.line_total}</td>\r\n		</tr>\r\n{foreachelse}\r\n		<tr>\r\n			<td colspan=\"4\">{lang}No Cart Items{/lang}</td>\r\n		</tr>\r\n{/foreach}\r\n\r\n		<tr class=\"cart_total\">\r\n			<td colspan=\"2\" width=\"100%\">&nbsp;</td>\r\n			<td class=\"total-name\">{lang}Shipping{/lang}:</td>\r\n			<td class=\"total-value\"><nobr>{$shipping_total}</nobr></td>\r\n		</tr>\r\n		<tr class=\"cart_total\">\r\n			<td colspan=\"2\">&nbsp;</td>\r\n			<td class=\"total-name\"><strong>{lang}Total{/lang}:</strong></td>\r\n			<td class=\"total-value\"><nobr>{$order_total}</nobr></td>\r\n		</tr>\r\n	</table>\r\n</div>\r\n{else}\r\n	{lang}No Cart Items{/lang}\r\n{/if}','2013-10-16 21:54:14','2013-10-16 22:08:18','shopping_cart'),
(11,'footer-links','<ul class=\"unstyled\">\r\n	{foreach from=$content_list item=node}\r\n		<li{if $node.alias == $content_alias} class=\"active\"{/if}><a href=\"{$node.url}\">{$node.name}</a></li>\r\n	{/foreach}\r\n</ul>','2013-10-08 12:52:16','2013-10-08 21:00:45','content_listing'),
(12,'featured-products','{if $content_list}\r\n<h2>{lang}Featured Products{/lang}</h2>\r\n<div class=\"row-fluid featured-products\">\r\n	<ul class=\"thumbnails\">\r\n	{foreach from=$content_list item=node}\r\n		<li class=\"span4 item\">\r\n			<div class=\"thumbnail\">\r\n				<a href=\"{$node.url}\" class=\"image\">\r\n					<img src=\"{$node.image}\" alt=\"{$node.name}\"{if isset($thumbnail_width)} width=\"{$thumbnail_width}\"{/if} />\r\n					<span class=\"frame-overlay\"></span>\r\n					<span class=\"price\">{$node.price}</span>\r\n				</a>\r\n				<div class=\"inner notop nobottom\">\r\n					<h4 class=\"title\">{$node.name}</h4>\r\n					<div class=\"description\">{$node.description|strip_tags|truncate:30:\"...\":true}</div>\r\n				</div>\r\n			</div>\r\n			{product_form product_id={$node.id}}\r\n			<div class=\"inner darken notop\">\r\n				<button class=\"btn btn-add-to-cart\" type=\"submit\" value=\"{lang}Buy{/lang}\"><i class=\"icon-shopping-cart\"></i> {lang}Buy{/lang}</button>\r\n			</div>\r\n			{/product_form}\r\n		</li>\r\n	{/foreach}\r\n	</ul>\r\n</div>\r\n{/if}  ','2013-10-08 18:09:58','2013-10-18 11:39:57','content_listing'),
(13,'slider','{if $content_list}\r\n<script type=\"text/javascript\" src=\"{base_path}/js/jquery/plugins/sequence.jquery-min.js\"></script>\r\n<script type=\"text/javascript\" src=\"{base_path}/js/jquery/plugins/sequencejs-options.js\"></script>\r\n\r\n<section id=\"slider\">\r\n	<div id=\"sequence-theme\">\r\n		<div id=\"sequence\">\r\n			<div class=\"prev\"><i class=\"icon-chevron-left\"></i></div>\r\n			<div class=\"next\"><i class=\"icon-chevron-right\"></i></div>\r\n				<ul>\r\n				{foreach from=$content_list item=node}\r\n					<li>\r\n						<div class=\"text\">\r\n							<h2 class=\"title\"><span>{$node.name|strip_tags|truncate:30:\"...\":true}</span></h2>\r\n							<h3 class=\"subtitle\"><span>{lang}best buy!{/lang}</span></h3>\r\n							<div class=\"description\">{$node.description|strip_tags|truncate:120:\"...\":true}</div>\r\n							<a href=\"{$node.url}\" class=\"btn\">{lang}read more{/lang}</a>\r\n						</div>\r\n						<img class=\"image\" src=\"{$node.image}\" alt=\"{$node.name}\" />\r\n					</li>\r\n				{/foreach}\r\n				</ul>\r\n		</div>\r\n	</div>\r\n</section>\r\n{/if}','2013-10-08 18:39:12','2013-10-19 23:54:34','content_listing')	;
