INSERT INTO `stylesheets` VALUES 
(1,1,'VamCart','vamcart','@import url(\'http://fonts.googleapis.com/css?family=Open+Sans:400,400italic,600,600italic,700,700italic\');\r\n\r\n@import \"animate.min.css\";\r\n\r\n/* ========================================================================== */\r\n/* Global properties (body, common classes, vertical rhythm, structure etc)\r\n/* ========================================================================== */\r\nbody {\r\n  font-family: \"Open Sans\", sans-serif !important;\r\n  background-color: #ffffff;\r\n  color: #909090;\r\n  padding: 0;\r\n}\r\n.inner {\r\n  background-color: #fbfbfb;\r\n  position: relative;\r\n  padding: 15px;\r\n}\r\n.inner.darken {\r\n  background-color: #eeeeee;\r\n}\r\n::selection {\r\n  background: #ff9f80;\r\n  color: #ffffff;\r\n  /* Safari */\r\n}\r\n::-moz-selection {\r\n  background: #ff9f80;\r\n  color: #ffffff;\r\n  /* Firefox */\r\n}\r\n/* main link color/style */\r\na {\r\n  -webkit-transition: all 0.3s ease-out;\r\n  -moz-transition: all 0.3s ease-out;\r\n  -ms-transition: all 0.3s ease-out;\r\n  -o-transition: all 0.3s ease-out;\r\n  transition: all 0.3s ease-out;\r\n  color: #222222;\r\n  text-decoration: none;\r\n}\r\na:hover {\r\n  -webkit-transition: all 0.3s ease-out;\r\n  -moz-transition: all 0.3s ease-out;\r\n  -ms-transition: all 0.3s ease-out;\r\n  -o-transition: all 0.3s ease-out;\r\n  transition: all 0.3s ease-out;\r\n  color: #ff6633;\r\n}\r\na.read-more {\r\n  text-decoration: none;\r\n  border-bottom: dotted 1px #ff6633;\r\n}\r\na.read-more:hover {\r\n  border-bottom-style: solid;\r\n}\r\n.italic {\r\n  color: #ff6633;\r\n  font-style: italic;\r\n}\r\nstrong {\r\n  color: #000000;\r\n}\r\n.underline:after {\r\n  background: none repeat scroll 0 0 #ff6633;\r\n  bottom: 0;\r\n  content: \"\";\r\n  display: block;\r\n  height: 2px;\r\n  margin-top: 10px;\r\n  width: 40px;\r\n}\r\nblockquote {\r\n  border-color: #ff6633;\r\n  border-left-width: 2px;\r\n}\r\nblockquote p {\r\n  font-size: 14px;\r\n  margin-b