(4, 1, 3, 0, 'Product Info', '<script type=\"text/javascript\">\r\n$(document).ready(function(){\r\n  $(\"a.lightbox\").lightBox({\r\n    fixedNavigation:true,\r\n      imageLoading: \"{base_path}/img/jquery/plugins/lightbox/lightbox-ico-loading.gif\",\r\n      imageBtnClose: \"{base_path}/img/jquery/plugins/lightbox/lightbox-btn-close.gif\",\r\n      imageBtnPrev: \"{base_path}/img/jquery/plugins/lightbox/lightbox-btn-prev.gif\",\r\n      imageBtnNext: \"{base_path}/img/jquery/plugins/lightbox/lightbox-btn-next.gif\",      \r\n    });\r\n});\r\n{if ($ajax_enable eq \'1\')}\r\n  function onProductFormSubmit() {\r\n    var str = $(\"#product-form\").serialize();\r\n\r\n    $.post(\"{base_path}/cart/purchase_product\", str, function(data) {\r\n      $(\"#shopping-cart-box\").html(data);\r\n    });\r\n  }\r\n{/if}\r\n</script>\r\n\r\n<!-- start: Page section -->\r\n	<section class=\"span9 page-sidebar pull-right\">\r\n\r\n		<div class=\"row-fluid\">\r\n			<!-- start: Product image -->\r\n				{content_images}\r\n			<!-- end: Product image -->\r\n			<!-- start: Product title -->\r\n				<div class=\"span6 product-info\">\r\n\r\n					<div class=\"inner product-title\">\r\n						<div class=\"row-fluid\">\r\n							<div class=\"span8 title\"><h1>{page_name}</h1></div>\r\n							<div class=\"span4 price\">{product_price}</div>\r\n						</div>\r\n					</div>\r\n\r\n					<div class=\"inner nobottom product-cart\">\r\n							{product_form}\r\n								<label>{lang}Qty{/lang}:</label>\r\n								<input name=\"product_quantity\" id=\"product_quantity\" type=\"text\" value=\"1\" size=\"3\" />\r\n								<button type=\"submit\" class=\"btn btn-inverse\"><i class=\"icon-shopping-cart\"></i> {lang}Add to cart{/lang}</button>\r\n							{/product_form}\r\n					</div>\r\n					\r\n					<div class=\"inner\">\r\n						{attribute_list value_attributes=$node.attributes}\r\n					</div>\r\n\r\n				</div>\r\n			<!-- end: Product title -->\r\n		</div>\r\n\r\n		<div class=\"row-fluid\">\r\n\r\n			<div class=\"row-fluid product-tabs\">\r\n				<section class=\"widget\">\r\n\r\n					<ul class=\"nav nav-tabs\">\r\n						<li class=\"active\"><a href=\"#description\" data-toggle=\"tab\">{lang}Description{/lang}</a></li>\r\n						<li><a href=\"#reviews\" data-toggle=\"tab\">{lang}Reviews{/lang}</a></li>\r\n						<li><a href=\"#add-review\" data-toggle=\"tab\">{lang}Add Review{/lang}</a></li>\r\n					</ul>\r\n\r\n					<div class=\"tab-content\">\r\n\r\n						<div class=\"tab-pane inner fade in notop active\" id=\"description\">\r\n							{description}\r\n						</div>\r\n\r\n						<div class=\"tab-pane inner fade in notop\" id=\"reviews\">\r\n							{module alias=\'reviews\' action=\'display\'}\r\n						</div>\r\n\r\n						<div class=\"tab-pane inner fade in notop\" id=\"add-review\">\r\n							{module alias=\'reviews\' action=\'create\'}\r\n						</div>\r\n\r\n					</div>\r\n				</section>\r\n			</div>\r\n		</div>\r\n\r\n		{xsell}\r\n\r\n	</section>\r\n<!-- end: Page section -->', '2013-10-01 16:07:25', '2013-04-17 16:07:25'),
